import BlockState from './BlockState';
import { OrderedMap as orderedMap } from 'immutable';
import generateKey from './generateKey';

/**
 * BlockMap class
 */
export default class BlockMap {

  constructor (immutable) {
    if (!immutable || !(immutable instanceof orderedMap)) {
      throw new Error('Please use `BlockMap.createFromArray` method');
    }

    this._immutable = immutable;
  }

  /** 
   * Get immutable for this blockMap
   */
  getImmutable () {
    return this._immutable;
  }

  toJS () {
    return this.getImmutable().toJS();
  }

  toJSON () {
    return this.getImmutable().toJSON();
  }

  toArray () {
    return this.getImmutable().toArray();
  }

  get () {
    return this.getImmutable().get(...arguments);
  }

  getIn () {
    return this.getImmutable().getIn(...arguments);
  }

  set () {
    let newImmutable = this.getImmutable().set(...arguments);
    this._immutable = newImmutable;
  }

  remove () {
    return this.getImmutable().delete(...arguments);
  }

  /**
   * Create from adapter
   * @param {DataAdapter} adapter
   */
  static create (adapter) {
    const entities = adapter.getEntities();
    return new BlockMap(orderedMap(adapter.getBlocks().map(function (blockText, i) {
      let blockObject = createBlockObject(adapter, i);
      blockObject.key = blockObject.key || generateKey();
      blockObject.text = blockText;
      blockObject.entity = entities.length > i ? entities[i] : null;
      return [blockObject.key, BlockState.create(blockObject)];
    })));
  }

  /**
   * Build the mapBlock from array
   * @deprecated
   * @param {object[]} blocks - Array of object.
   * @return {object} `Immutable.OrderedMap` object.
   */
  static createFromArray (blocks) {
    return new BlockMap(orderedMap(blocks.map(function (block) {
      block.key = block.key || generateKey();
      return [block.key, BlockState.create(block)];
    })));
  }
}

function createBlockObject (adapter, index) {
  let inlineStyles = adapter.getInlineStyles();
  let blockTypes = adapter.getBlockTypes();
  let blockDepths = adapter.getBlockDepths();

  let style = inlineStyles[index];
  let type = blockTypes[index];
  let depth = blockDepths[index];

  return {
    style,
    type,
    depth
  };
}

import { Record as record, List as list } from 'immutable';
import blockType from './blockType';
import inlineType from './inlineType';
import objectFilter from './util/objectFilter';

/**
 * @private
 * @constant
 */
const persistent = {
  key: '',
  type: blockType.UNSTYLED,
  characterList: list(),
  depth: 0,
  text: ''
};

/**
 * @private
 */
const CharacterListRecord = record({
  style: inlineType.NONE,
  entity: null
});

/**
 * @private
 */
const Base = record(persistent);

/**
 * This is a `Immutable.Record` constructor.
 */
export default class BlockState extends Base {
  /**
   * Get the key from the immutable.
   */
  getKey () {
    return this.get('key');
  }

  /**
   * Get the type from the immutable.
   */
  getType () {
    return this.get('type');
  }

  /**
   * Get the character list from immutable.
   */
  getCharacterList () {
    return this.get('characterList');
  }

  /**
   * Get the depth from immutable.
   */
  getDepth () {
    return this.get('depth');
  }

  /**
   * Get the text from immutable.
   * The text is the content of the block.
   */
  getText () {
    return this.get('text');
  }

  /**
   * Create a new `BlockState`.
   * @return {object} `BlockState` object.
   */
  static create (obj) {
    const text = obj.text;
    const inlineStyleRanges = obj.style || [];
    const entity = obj.entity || {};
    if (!entity.length) {
      entity.offset = -1;
      entity.length = -1;
    }

    const charList = list(text.split('').map(function (_, i) {
      if (i >= entity.offset && i < (entity.offset + entity.length)) {
        return new CharacterListRecord({entity: entity.key});
      }

      return new CharacterListRecord();
    }));

    obj.characterList = applyInlineStyleRangeToList(charList, inlineStyleRanges);
    return new BlockState(objectFilter(obj, persistent));
  }
}

/**
 * ranges is an array of object, like
 * [{offset: 2, length: 4, style: 0}]
 * @param {array} list
 * @param {array} ranges
 */
function applyInlineStyleRangeToList (charList, ranges) {
  if (!ranges.length) return charList;

  return charList.withMutations(function (mutableList) {
    ranges.forEach(function (range) {
      for (let i = range.offset; i < (range.offset + range.length); i++) {
        const style = mutableList.get(i).get('style') + range.style;
        const entity = mutableList.get(i).get('entity');
        mutableList.set(i, new CharacterListRecord({entity: entity, style: style}));
      }
    });
  });
}

import { List as list, Record as record } from 'immutable';

/**
 * @private
 * @constant
 */
const persistentOfBlockTree = {
  leafs: null,
  start: 0,
  end: 0
};

/**
 * @private
 * @constant
 */
const persistentOfLeaf = {
  start: 0,
  end: 0
};

/**
 * @private
 */
const BlockTreeRecord = record(persistentOfBlockTree);
const LeafRecord = record(persistentOfLeaf);

/**
 * @public
 * @property {function} create - create a blockTree object.
 */
export default class BlockTree {

  constructor () {
    throw new Error('Please use the static methods');
  }

  /**
   * Block is a BlockState
   * @return {object} `Immutable.List` object.
   */
  static create (block) {
    if (!block.size) {
      // return empty
      return emptyBlockTree();
    }

    let leafs = [];
    let branchs = [];
    let leaf = {start: 0, end: -1};
    let previousStyle = block.getCharacterList().first().get('style');
    let previousEntity = block.getCharacterList().first().get('entity');
    let start = 0;

    // <p>hello world hel<em>lo <b>wor</b></em><b>ld</b> hello world hello world</p>
    block.getCharacterList().forEach(function (ch, i) {
      if (ch.get('entity') !== previousEntity) {
        leaf.end = i;
        leafs.push(new LeafRecord(leaf));
        branchs.push(new BlockTreeRecord({
          leafs: list(leafs.slice()),
          start: start,
          end: i
        }));
        previousEntity = ch.get('entity');
        previousStyle = ch.get('style');
        leafs = [];
        leaf.start = i;
        leaf.end = -1;
        start = i;
      }

      if (leaf.start !== i && ch.get('style') !== previousStyle) {
        leaf.end = i;
        leafs.push(new LeafRecord(leaf));
        leaf.start = i;
        leaf.end = -1;
        previousStyle = ch.get('style');
      }
    });

    if (leaf.start <= block.getCharacterList().size - 1) {
      leaf.end = block.getCharacterList().size;
      leafs.push(new LeafRecord(leaf));
    }


    if (leafs.length) {
      branchs.push(new BlockTreeRecord({
        leafs: list(leafs),
        start: leaf.start,
        end: block.getCharacterList().size 
      }));
    }

    return list(branchs);
  }
}

/**
 * create a empty block tree
 */
function emptyBlockTree () {
  return list.of(new BlockTreeRecord({
    leafs: list.of(new LeafRecord())
  }));
}

/**
