import { Record as record, List as list } from 'immutable';
import blockType from './blockType';
import inlineType from './inlineType';
import objectFilter from './util/objectFilter';

/**
 * @private
 * @constant
 */
const persistent = {
  key: '',
  type: blockType.UNSTYLED,
  characterList: list(),
  depth: 0,
  text: ''
};

/**
 * @private
 */
const CharacterListRecord = record({
  style: inlineType.NONE
});

/**
 * @private
 */
const Base = record(persistent);

/**
 * This is a `Immutable.Record` constructor.
 */
export default class BlockState extends Base {
  /**
   * Get the key from the immutable.
   */
  getKey () {
    return this.get('key');
  }

  /**
   * Get the type from the immutable.
   */
  getType () {
    return this.get('type');
  }

  /**
   * Get the character list from immutable.
   */
  getCharacterList () {
    return this.get('characterList');
  }

  /**
   * Get the depth from immutable.
   */
  getDepth () {
    return this.get('depth');
  }

  /**
   * Get the text from immutable.
   * The text is the content of the block.
   */
  getText () {
    return this.get('text');
  }

  /**
   * Create a new `BlockState`.
   * @return {object} `BlockState` object.
   */
  static create (obj) {
    const text = obj.text;
    const inlineStyleRanges = obj.inlineStyleRanges || [];

    const charList = list(text.split('').map(function () {
      return new CharacterListRecord();
    }));

    obj.characterList = applyInlineStyleRangeToList(charList, inlineStyleRanges);
    return new BlockState(objectFilter(obj, persistent));
  }
}

/**
 * ranges is an array of object, like
 * [{offset: 2, length: 4, style: 0}]
 * @param {array} list
 * @param {array} ranges
 */
function applyInlineStyleRangeToList (charList, ranges) {
  if (!ranges.length) return charList;

  return charList.withMutations(function (mutableList) {
    ranges.forEach(function (range) {
      for (let i = range.offset; i < (range.offset + range.length); i++) {
        const style = mutableList.get(i).get('style') + range.style;
        mutableList.set(i, new CharacterListRecord({style: style}));
      }
    });
  });
}

/**
 * Simple base DataAdapter
 */

export default class DataAdapter {
  constructor (data = {}) {
    this._data = data;
  }

  /**
   * @param {object} data The data source
   */
  setData (data) {
    this._data = data;
  }

  /**
   * Must return array
   * @return {array}
   */
  getBlocks () {
    return this._data.blocks || [];
  }
}

import { Record as record } from 'immutable';
import blockMapBuilder from './blockMapBuilder';
import treeMapBuilder from './treeMapBuilder';

const persistent = {
  treeMap: null,
  blockMap: null
};

const StateRecord = record(persistent);

/**
 * The state for the whole editor.
 */
export default class EditorState {
  constructor (immutable) {
    if (!immutable || !(immutable instanceof StateRecord)) {
      throw new Error('EditorState requires an `StateRecord` instance');
    }
    this._immutable = immutable;
  }

  /**
   * Get the immutable object
   */
  getImmutable () {
    return this._immutable;
  }

  /**
   * @see {@link http://facebook.github.io/immutable-js}
   */
  toJS () {
    return this._immutable.toJS();
  }

  /**
   * @see {@link http://facebook.github.io/immutable-js}
   */
  toJSON () {
    return this._immutable.toJSON();
  }

  /**
   * Get blockMap from immutable
   */
  getBlockMap () {
    return this.getImmutable().get('blockMap');
  }

  /**
   * Get treeMap from immutable
   */
  getTreeMap () {
    return this.getImmutable().get('treeMap');
  }

  /**
   * from the treeMap, get a tree by a key
   * @key {string}
   */
  getBlockTreeForKey (key) {
    return this.getTreeMap().get(key);
  }

  /**
   * Create an `EditorState` object.
   * @param {array} blocks
   */
  static create (blocks) {
    let blockMap = blockMapBuilder.createFromArray(blocks);
    let treeMap = treeMapBuilder.createFromBlockMap(blockMap);

    return new EditorState(new StateRecord({blockMap, treeMap}));
  }

  /**
   * Create an empty `EditorState` object
   */
  static createEmpty () {
  }
}

import BlockState from './BlockState';
import { OrderedMap as orderedMap } from 'immutable';
import generateKey from './generateKey';

/**
 * Build the mapBlock from array
 * @public
 * @param {object[]} blocks - Array of object.
 * @return {object} `Immutable.OrderedMap` object.
 */
function createFromArray (blocks) {
  return orderedMap(blocks.map(function (block) {
    block.key = block.key || generateKey();
    return [block.key, BlockState.create(block)];
  }));
}

/**
 * module, see source.
 */
const blockMapBuilder = {
  createFromArray
};

export default blockMapBuilder;

import blockType from './blockType';
import ContentBlock from './components/ContentBlock';

/**
 * Determine which component should we use for render the
 * current block.
 *
 * @param {BlockState} block - The block has getType method
 * that return a type of blockType.
 * @return {null|object}
 */
export default function blockRenderer (block) {
  const type = block.getType();

  switch (type) {
  case blockType.MEDIA:
    // not supported
    return null;
  default:
    return ContentBlock;
  }
}

/**
 * Get the parent element type for the block.
 * if the block's element type is `li`, then it
 * must be wrapped by `ul` element.
 * @param {number} type - one of `blockType`
 * @return {null|string}
 */
export function getParentElementForBlockOfType (type) {
  switch (type) {
  case blockType.UNORDERED_LIST_ITEM:
    return 'ul';
  case blockType.ORDERED_LIST_ITEM:
    return 'ol';
  case blockType.CODE:
    return 'pre';
  default:
    return null;
  }
}

/**
 * Get the element type for the block.
 * @param {number} type - one of `blockType`
 * @return {string}
 */
export function getElementForBlockOfType (type) {
  switch (type) {
  case blockType.UNORDERED_LIST_ITEM:
  case blockType.ORDERED_LIST_ITEM:
    return 'li';
  case blockType.HEADER_ONE:
    return 'h1';
  case blockType.HEADER_TWO:
    return 'h2';
  case blockType.BLOCKQUOTE:
    return 'blockquote';
  default: 
    return 'div';
  }
}

import { List as list, Record as record } from 'immutable';

/**
 * @private
 * @constant
 */
const persistentOfBlockTree = {
  leafs: null
};

/**
 * @private
 * @constant
 */
const persistentOfLeaf = {
  start: 0,
  end: 0
};

/**
 * @private
 */
const BlockTreeRecord = record(persistentOfBlockTree);
const LeafRecord = record(persistentOfLeaf);

/**
 * @public
 * @property {function} create - create a blockTree object.
 */
let blockTree = {
  /**
   * Block is a BlockState
   * @return {object} `Immutable.List` object.
   */
  create (block) {
    if (!block.size) {
      // return empty
      return emptyBlockTree();
    }

    let leafs = [];
    let leaf = {start: 0, end: -1};
    let start = 0;
    let previousStyle = block.getCharacterList().first().get('style');

    block.getCharacterList().forEach(function (ch) {
      if (start === block.getCharacterList().size - 1) {
        leaf.end = start + 1;
        leafs.push(new LeafRecord(leaf));
      }

      if (ch.get('style') !== previousStyle) {
        leaf.end = start;
        leafs.push(new LeafRecord(leaf));
        leaf = {start: start, end: -1};
        previousStyle = ch.get('style');
      }
      start += 1;
    });

    return list.of(new BlockTreeRecord({
      leafs: list(leafs)
    }));
  }
};

export default blockTree;

/**
 * create a empty block tree
 */
function emptyBlockTree () {
  return list.of(new BlockTreeRecord({
    leafs: list.of(new LeafRecord())
  }));
}

/**
 * @public
 * @readonly
 * @enum {number}
 */
const blockType = {
  UNSTYLED: 0,
  PARAGRAPH: 1,
  UNORDERED_LIST_ITEM: 2,
  ORDERED_LIST_ITEM: 3,
  BLOCKQUOTE: 4,
  HEADER_ONE: 5,
  HEADER_TWO: 6,
  CODE: 7,
  MEDIA: 8
};

export default blockType;

