import React, { Component } from 'react';
import { getElementForBlockOfType } from '../blockRenderer';
import BlockLeaf from './BlockLeaf';

export default class ContentBlock extends Component {

  static displayName = 'ContentBlock';

  constructor (props) {
    super(props);
  }

  _renderChild () {
    const tree = this.props.tree;
    const block = this.props.block;
    const text = block.getText();
    const key = block.getKey();

    return tree.map(function (branch, i) {
      const leafs = branch.get('leafs');

      return leafs.map(function (leaf, j) {
        const start = leaf.get('start');
        const end = leaf.get('end');
        const leafText = text.slice(start, end);
        const inlineParentEle = 'span';

        return React.createElement(inlineParentEle, {
        }, React.createElement(BlockLeaf, {
          text: leafText
        }));
      }.bind(this)).toArray();
    }.bind(this)).toArray();
  }

  render () {
    const block = this.props.block;
    const elementType = getElementForBlockOfType(block.getType());

    return React.createElement(elementType, {
      'data-block': true
    }, this._renderChild());
  }
}

ContentBlock.propTypes = {
  block: React.PropTypes.object.isRequired,
  tree: React.PropTypes.object.isRequired
};

import React, { Component } from 'react';
import EditorState from '../EditorState';
import ass from '../util/ass';
import richTextEditorHandler from '../richTextEditorHandler';
import EditorContent from './EditorContent';

export default class Editor extends Component {
  constructor (props) {
    super(props);

    // keyboard handler
    this._handler = null; 
  }

  componentDidMount () {
    this._handler = richTextEditorHandler;
  }

  getDefaultAttrs () {
    return {
      contentEditable: true,
      spellCheck: true,
      style: {
        outline: 'none'
      },

      onKeyDown: setHandler('onKeyDown', this)
    };
  }

  render () {
    return (
      <div {...this.getDefaultAttrs()} className={ass('editor')}>
        <EditorContent editorState={this.props.editorState} />
      </div>
    );
  }
}

Editor.propTypes = {
  editorState: React.PropTypes.instanceOf(EditorState).isRequired
};

function setHandler (name, context) {
  return function (e) {
    if (this._handler && this._handler[name]) {
      const handler = this._handler[name];
      if (typeof handler === 'function') {
        handler.call(this, e);
      }
    }
  }.bind(context || this);
}

import React, { Component } from 'react';
import EditorState from '../EditorState';
import ass from '../util/ass';
import defaultBlockRenderer, { getParentElementForBlockOfType } from '../blockRenderer';

export default class EditorContent extends Component {
  constructor (props) {
    super(props);
  }

  _renderChild () {
    const editorState = this.props.editorState;
    const blockMap = editorState.getBlockMap();
    const blockMapAsArray = blockMap.toArray();
    const blockRenderer = this.props.blockRenderer;

    let renderedBlocks = [];
    let blockStateItem;
    let lastParentElement = null;
    let parentElement = null;
    let childsOfParentElement = null;

    for (let i = 0; i < blockMapAsArray.length; i++) {
      blockStateItem = blockMapAsArray[i];

      const key = blockStateItem.getKey();
      let tree = editorState.getBlockTreeForKey(key);
      let BlockComponent = blockRenderer(blockStateItem);

      // currently, just ignore it if we do not support that 
      // block type
      if (BlockComponent === null) {
        continue;
      }

      let renderedBlock = <BlockComponent block={blockStateItem} key={key} {...this.props} tree={tree} />;

      parentElement = getParentElementForBlockOfType(blockStateItem.getType());
      if (parentElement !== null) {
        if (parentElement === lastParentElement && childsOfParentElement !== null) {
          childsOfParentElement.push(renderedBlock);
        } else {
          childsOfParentElement = [];

          let renderedParent = React.createElement(parentElement, {
            'key': key + '-wrap'
          }, childsOfParentElement);

          childsOfParentElement.push(renderedBlock);
          renderedBlocks.push(renderedParent);

          lastParentElement = parentElement;
          parentElement = null;
        }
      } else {
        renderedBlocks.push(renderedBlock);
        childsOfParentElement = null;
        lastParentElement = null;
        parentElement = null;
      }
    }

    return renderedBlocks;
  }

  render () {
    return (
      <div className={ass('editorContent')}>
        {this._renderChild()}
      </div>
    );
  }
}

EditorContent.propTypes = {
  editorState: React.PropTypes.instanceOf(EditorState).isRequired,
  blockRenderer: React.PropTypes.func
};

EditorContent.defaultProps = {
  blockRenderer: defaultBlockRenderer
};

let used = {};
let hasOwnProp = Object.prototype.hasOwnProperty;

/**
 * Generate a random key.
 * @return {string}
 */
export default function () {
  let key = ('0000' + (Math.random() * Math.pow(36, 4) << 0).toString(36)).slice(-4);
  while (hasOwnProp.call(used, key)) {
    key = ('0000' + (Math.random() * Math.pow(36, 4) << 0).toString(36)).slice(-4);
  }

  used[key] = true;
  return key;
}

