import { OrderedMap as orderedMap } from 'immutable';
import blockMapBuilder from './blockMapBuilder';
import blockTree from './blockTree';

/**
 * Create `treeMap` from array.
 * @param {array} blocks 
 */
function createFromArray (blocks) {
  let blockMap = blockMapBuilder.createFromArray(blocks);

  return createFromBlockMap(blockMap);
}

/**
 * create `treeMap` from `blockMap`
 * @param {object} blockmap
 */
function createFromBlockMap (blockMap) {
  return orderedMap(blockMap.map(function (block) {
    return blockTree.create(block);
  }));
}

/**
 * module, see source.
 */
let treeMapBuilder = {
  createFromArray,
  createFromBlockMap
};

export default treeMapBuilder;

const prefix = '_RcmVh';

/**
 * Construct a class with a prefix.
 * @return {string}
 */
export default function (a) {
  return prefix + '-' + a;
}

/**
 * @return {string}
 */
export function getPrefix () {
  return prefix;
}

export default function empty () {}

// do not export this explict
empty.withReturn = function (v) {
  return function () {
    return v;
  };
};

/**
 * `danger` object may contains some attribute 
 * that we do not want.
 * @param {object} danger The object that needs filter.
 * @param {object} defaults
 */
export default function (danger, defaults) {
  let rets = {};

  Object.keys(danger).map(function (key) {
    if (key in defaults) {
      rets[key] = danger[key];
    }
  });

  return rets;
}
